drop table hrsights.tb_dependentes cascade;drop table hrsights.tb_funcionarios cascade;drop table hrsights.tb_registros cascade;drop table hrsights.tb_salarios cascade;drop table hrsights.tb_cargos cascade;drop table hrsights.tb_empresa cascade;create table hrsights.tb_empresa(  cnpj varchar(100) primary key,  razao_social varchar(100));create table hrsights.tb_cargos(  cbo int primary key,  nome_cargo varchar(100));create table hrsights.tb_salarios(  id_salario int primary key generated always as identity,  id_cargo int,  salario_numerico numeric);create table hrsights.tb_registros(  cpf varchar(50) primary key,  acoes varchar(100),  cnpj varchar(50),  constraint fk_registros   foreign key (cnpj)   references hrsights.tb_empresa(cnpj));create table hrsights.tb_funcionarios(  cpf varchar(11) primary key,  rg varchar(7),  data_nascimento date,  endereco varchar(100),  titulo_eleitor varchar(50),  id_salario int,  nome_completo varchar(50),  id_cargo int,  cbo int,  numero_telefone numeric(11),  cnpj varchar(50),  constraint id_cnpj   foreign key (cnpj)   references hrsights.tb_empresa(cnpj),  constraint id_cargo   foreign key (id_cargo)   references hrsights.tb_cargos(cbo) );create table hrsights.tb_dependentes(  rg varchar(7) primary key,  nome_completo varchar(50),  data_nascimento date,  nome_dependente varchar(50));